INSERT INTO rsatom.qualification(id, name, type, description)
VALUES (1, 'Вывод из эксплуатации объектов использования атомной энергии', 'TEAM',
        'Процесс, состоящий из комплекса мероприятий в части принятия технических решений по выработке концепции, которая должна составлять основу проекта вывода из эксплуатации ОИАЭ, обеспечивающая наиболее экономически эффективное проведение ВЭ.'),
       (2, 'Инженерное мышление Каракури', 'TEAM',
        'Каракури – это механическое устройство, предназначенное для упрощения трудоемких операций и устранения потерь. Соревнования по компетенции представляют собой конкурентную работу участников, реализацию работающего макета устройства каракури, разработку руководства по эксплуатации устройства.'),
       (3, 'Информационная безопасность (анализ защищенности данных от внешних угроз)', 'TEAM', null),
       (4, 'Корпоративная защита от внутренних угроз информационной безопасности', 'TEAM', null),
       (5, 'Цифровое ПСР-предприятия', 'TEAM', null),
       (6, 'Математическое моделирование', 'TEAM', null),
       (7, 'Инженерное проектирование', 'TEAM',
        'Компетенция нацелена на создание среды профессионального роста, в которой участники демонстрируют и повышают свои навыки в проектировании зданий и сооружений различного назначения, включая сети инженерно-технического обеспечения и системы инженерно-технического обеспечения с использованием технологии информационного моделирования.'),
       (8, 'Технологии композитов', 'TEAM',
        'Компетенция предполагает изготовление изделия из полимерного композита с применением инженерных, производственных, технологических и организационных приёмов, инструментов и навыков, задействованных в проектировании, технологической подготовке, изготовлении и испытании полученного изделия командой взаимодополняющих специалистов.'),
       (9, 'Управление жизненным циклом', 'TEAM',
        'Компетенция предполагает решение инженерной задачи, которая предусматривает исследование и анализ рынка, разработку конструкторской и технологической документации, проработку дизайна изделия, оценку инвестиционной привлекательности проекта.'),
       (10, 'Программные решения для бизнеса', 'TEAM',
        'Разработка программных продуктов требует широких познаний в области информационных технологий. Разработчики занимаются сложным комплексом задач, связанным с анализом предметной области, проектированием и разработкой различных версий программных продуктов.'),
       (11, 'Промышленная механика и монтаж (ремонт и наладка промышленного оборудования)', 'TEAM',
        '(Ремонт и наладка промышленного оборудования)* - При неправильном использовании оборудования, ошибках в действиях операторов промышленного оборудования, а также износе при долгом использовании, на производстве возникают неисправности оборудования. Для устранения неисправностей и поддержания оборудования в работоспособном состоянии на предприятиях созданы ремонтные службы.'),
       (12, 'Геодезия', 'TEAM',
        'Чтобы участвовать в компетенции, специалист должен уметь читать проектную и рабочую документации, владеть навыками работы с современными геодезическими средствами измерений, обеспечивать площадку строительства геодезической разбивочной основой, сопровождать строительство точными координатами и высотами, формировать исполнительные геодезические схемы и многие другие задачи.'),

       (13, 'Инженер-технолог машиностроения', 'INDIVIDUAL',
        'Специалист в данной области занимается разработкой и организацией производственных и технологических процессов в соответствии с требованиями нормативной документации. В сферу его деятельности входит подбор необходимых инструментов, оборудования и технологической оснастки, определение последовательности обработки деталей и сборки узлов машин, контроль за качеством и эффективностью выполнения, определение оптимального размещения оборудования на производстве.'),
       (14, 'Аналитический контроль (лабораторный химический анализ)', 'INDIVIDUAL', null),
       (15, 'Изготовление прототипов', 'INDIVIDUAL', null),
       (16, 'Инженер-конструктор (инженерный дизайн CAD)', 'INDIVIDUAL', null),
       (17, 'Мехатроника', 'INDIVIDUAL', null),
       (18, 'Сварочные технологии', 'INDIVIDUAL', null),
       (19, 'Токарные работы на станках с ЧПУ', 'INDIVIDUAL', null),
       (20, 'Фрезерные работы на станках с ЧПУ', 'INDIVIDUAL', null),
       (21, 'Электромонтаж', 'INDIVIDUAL', null),
       (22, 'Электроника', 'INDIVIDUAL', null),
       (23, 'Аддитивные технологии', 'INDIVIDUAL', null),
       (24, 'Общестроительные работы (Бетонные строительные работы)', 'INDIVIDUAL', null),
       (25, 'Водитель спец. автомобиля', 'INDIVIDUAL', null),
       (26, 'Строительный контроль', 'INDIVIDUAL', null),
       (27, 'Управление качеством', 'INDIVIDUAL', null),
       (28, 'Машинное обучение и большие данные', 'INDIVIDUAL',
        'В рамках компетенции применяются наиболее эффективные алгоритмы машинного обучения, реализуется опыт их практического применения. Рассматривается применение машинного обучения к практическим новым задачам, требующим быстрого и эффективного решения.'),
       (29, 'Охрана  окружающей среды', 'INDIVIDUAL',
        '(Эколог) – Компетенция соответствует профессиональной деятельности специалиста по охране окружающей среды, который выполняет комплекс организационных и технических мер, направленных на обеспечение экологической безопасности, минимизацию негативного воздействия хозяйственной и иной деятельности на окружающую среду.'),
       (30, 'Работы на токарных универсальных станках', 'INDIVIDUAL',
        'Токарные станки позволяют изготавливать сложные детали и получать огромный навык в обработке материалов. Поэтому данный вид работы с металлом продолжает развиваться, несмотря на и без того высокий уровень, позволяющий обеспечить высочайшее качество и скорость обработки.'),
       (31, 'Работы на фрезерных универсальных станках', 'INDIVIDUAL',
        'Фрезерные станки позволяют изготавливать сложные детали и получать огромный навык в обработке материалов. Поэтому данный вид работы с металлом продолжает развиваться, несмотря на и без того высокий уровень, позволяющий обеспечить высочайшее качество и скорость обработки'),
       (32, 'Сметное дело', 'INDIVIDUAL',
        'Специалисты компетенции отвечают за разработку, проверку и анализ сметной документации любой сложности на основании исходных данных, в рамках технического задания и действующей нормативной документации.'),
       (33, 'Технологические системы энергетических объектов', 'INDIVIDUAL',
        'Специалисты компетенции решают комплексные инженерные задачи при проектировании, вводе в работу и эксплуатации основных и вспомогательных технологических систем (оборудования) различных типов энергетических объектов, а также выявляют проблемные вопросы эксплуатации с целью предотвращения нарушений в работе энергетических объектов.'),
       (34, 'Радиационный контроль', 'INDIVIDUAL',
        '(Дозиметрист) - Компетенция представляет собой совокупность навыков работника по проведению дозиметрических и радиометрических измерений различных видов ионизирующих излучений, определению показателей радиационной безопасности, проведению радиационного контроля, обработке результатов измерений, составлению документации, освоению новых методик в области радиационной безопасности, контролю проведения и организации работ в условиях радиационной опасности, правильному выбору и использованию средств индивидуальной защиты.'),
       (35, 'Неразрушающий контроль', 'INDIVIDUAL', null),
       (36, 'Обслуживание и ремонт релейной защиты и автоматики', 'INDIVIDUAL',
        'Основным назначением релейной защиты является выявление места возникновения КЗ и быстрое автоматическое отключение выключателей поврежденного оборудования или участка сети, выявление нарушений нормальных режимов работы оборудования и подача предупредительных сигналов обслуживающему персоналу или отключение оборудования с выдержкой времени.'),
       (37, 'Охрана труда', 'INDIVIDUAL',
        'Компетенция предполагает наличие знаний и навыков по таким направлениям, как обеспечение функционирования системы управления охраной труда, организация обучения по охране труда, обеспечение безопасных условий труда, организация расследования несчастных случаев и микротравм на производстве, идентификация и управление профессиональными рисками на рабочих местах, совершенствование культуры безопасности, оказание первой помощи, а также знание и умение грамотно применять нормативную документацию по охране труда при осуществлении контроля соблюдения требований охраны труда.'),
       (38, 'Промышленная автоматика', 'INDIVIDUAL',
        'Компетенция включает в себя элементы электромонтажа, монтажа систем автоматизации и мехатроники. Необходимы знания основ электромонтажа и систем автоматизации, навык работы с металлом, программирования, проектировки схем, умение находить неисправности в настройках и монтаже.'),

       (39, 'Квантовые технологии', 'STUDENT',
        'Компетенция решает вопросы информационной безопасности, высокоскоростной обработки больших объемов данных, сверхточных измерений малых величин, моделирования сложных гетероструктур различных материалов и сложных молекул и др. В ходе соревнований участники демонстрируют умение работать на современных системах квантового распределения ключа и квантовых компьютерах.');

